    }

    handleNameChange(e){
        this.setState({updatedName:e.target.value});
    }

    handleAgeChange(e){
        this.setState({updatedAge:e.target.value});
    }

    render(){

        if(this.props.state.update){
            console.log(this.props.state)
            return ( 
                <form className="form-inline">
                    <div className="form-group">
                        <label for="name">Name:</label>
                        <input id="name" type="text" className="form-control" value={this.props.state.userData.data.username} placeholder="Enter Username" onChange={this.handleNameChange}></input>
                    </div>

                    <div className="form-group">
                        <label >Age:</label>
                        <input type="number" className="form-control" value={this.props.state.userData.data.age} placeholder="Enter age" onChange={this.handleAgeChange}></input>
                    </div>

                    <div className="form-group">
                        <button type="submit" className="btn btn-primary">Update</button>
                    </div>

                </form>
            );  
        }
        else {
            return null;
        }

    }
}

export default UpdateUser;
