import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'
import AddUser from './AddUser';
import ViewUser from './ViewUser';
import UpdateUser from './UpdateUser';
import DeleteUser from './DeleteUser';

class App extends Component {
  render() {


    return (
      <Router>
        <div>
          <Route exact path="/" component={Home} />
          <Route path="/addUser" component={AddUser} />
          <Route path="/viewUser" component={ViewUser} />
          <Route path="/updateUser" component={UpdateUser} />
          <Route path="/DeleteUser" component={DeleteUser} />
        </div>
      </Router>    
    );
  }
}

class Home extends Component {
  render() {
    return (
      <div class="container">
        <div class="jumbotron">
          <h1 class="text-center">Admin Panel for Users</h1>
        </div>
        <LinkButtons />
      </div>
    );
  }
}

class AdminButtons extends Component {
  render(){
    return(
      <div class="row">
        <div class="col-md-3">
          <a href="/addUser" class="btn btn-success">Add User</a>
        </div>
        <div class="col-md-3">
          <a href="/viewUser" class="btn btn-info">View User</a>
        </div>
        <div class="col-md-3">
          <a href="/updateUser" class="btn btn-primary">Update User</a>
        </div>
        <div class="col-md-3">
          <a href="/deleteUser" class="btn btn-danger">Delete User</a>
        </div>
      </div>
    );
  }
}

class LinkButtons extends Component {
  render() {
    return (
      <div class="row">
        <div class="col-md-3">
          <Link class="btn btn-success" to="/addUser" style={{borderRadius:15}}>Add User</Link>
        </div>
        <div class="col-md-3">
          <Link class="btn btn-info" to="/viewUser" style={{borderRadius:15}}>View User</Link>
        </div>
        <div class="col-md-3">
          <Link class="btn btn-primary" to="/updateUser" style={{borderRadius:15}}>Update User</Link>
        </div>
        <div class="col-md-3">
          <Link class="btn btn-danger" to="/deleteUser" style={{borderRadius:15}}>Delete User</Link>
        </div>
      </div>
    );
  }
}


export default App;
