import React, { Component } from 'react';

class AddUser extends Component {

    componentDidMount() {
        document.title = 'Add User';
    }
    render(){
        return(

            <div className="container">
                <div className="jumbotron">
                    <h1 className="text-center">Add User</h1>
                </div>
                <UserForm />
            </div>
        );

    }
}

class UserForm extends Component {

    constructor(props){
        super(props);
        this.state = {
            name:'',
            age:'',
            nameErr:'',
            ageErr:''
        };
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleAgeChange = this.handleAgeChange.bind(this);
        this.handleForm = this.handleForm.bind(this);
    }

    handleNameChange(e){
        this.setState({name:e.target.value});
    }

    handleAgeChange(e){
        this.setState({age:e.target.value});
    }

    handleForm(){

        if(this.state.name===''){
            this.setState({nameErr:'Name is Required'});
        }else{
            this.setState({nameErr:''});
        }

        if(this.state.age===''){
            this.setState({ageErr:'Age is Required'});
        }else{
            this.setState({ageErr:''});
        }
    }

    render() {
        return (
            <div>

                <div className="form-group">
                    <label >Name:</label>
                    <input type="text" className="form-control" placeholder="Enter your name" onChange={this.handleNameChange}></input>
                </div>
                <span className="text-danger">{this.state.nameErr}</span>
                <div className="form-group">
                    <label>Age:</label>
                    <input type="number" className="form-control" placeholder="Enter your age" onChange={this.handleAgeChange}></input>
                </div>
                <span className="text-danger">{this.state.nameErr}</span>
                <div className="form-group">
                    <button className="btn btn-success" style={{borderRadius:15}} onClick={this.handleForm}>Submit</button>
                </div>


            </div>
        );
    }
}

export default AddUser;
