import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'
import AddUser from './AddUser';
import ViewUser from './ViewUser';
import UpdateUser from './UpdateUser';
import DeleteUser from './DeleteUser';

class App extends Component {
  render() {    
    return  <Home />;
  }
}

class Home extends Component {
  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="text-center">Admin Panel for Users</h1>
        </div>
        <AdminButtons />
        <Routing />
      </div>
    );
  }
}

class AdminButtons extends Component {
  render(){
    return(
      <div className="row">
        <div className="col-md-3">
          <a href="/addUser" className="btn btn-success">Add User</a>
        </div>
        <div class="col-md-3">
          <a href="/viewUser" className="btn btn-info">View User</a>
        </div>
        <div class="col-md-3">
          <a href="/updateUser" className="btn btn-primary">Update User</a>
        </div>
        <div class="col-md-3">
          <a href="/deleteUser" className="btn btn-danger">Delete User</a>
        </div>
      </div>
    );
  }
}

class Routing extends Component {
  render(){
    const routing = (
      <Router>
          <Route path="/" component={Home} />
          <Route path="/addUser" component={AddUser} />
          <Route path="/viewUser" component={ViewUser} />
          <Route path="/updateUser" component={UpdateUser} />
          <Route path="/DeleteUser" component={DeleteUser} />
      </Router>
    );
    const routes = routing.map((route)=>route);
    return (
      <div>
        {routes}
      </div>
    );
  }
}

export default App;
