import React, { Component } from 'react';
import axios from 'axios';

class DeleteUser extends Component {

    constructor(props){
        super(props);
        this.state = {
            userId:'',
            userIdErr:'',
            delete:false
        };
    }

    componentDidMount() {
        document.title = 'Delete User';
    }

    render() {

        return(
            <div className="container">
                <div className="jumbotron">
                    <h1 className="text-center">Delete User</h1>
                </div>
                <div className="form-inline">
                    <div className="form-group">
                        <label>User ID:</label>
                        <input type="text" className="form-control" placeholder="Enter User ID to delete User" value={this.state.userId} onChange={this.handleUserIdChange}></input>
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" onClick={this.handleSearch}>Search & Delete</button>
                    </div>
                </div>
                <span className="text-danger">{this.state.userIdErr}</span>
                <br />

                <SearchedUser state={this.state} nameHandler={this.nameHandler} ageHandler={this.ageHandler} />

            </div>
        );
    }
}

export default DeleteUser;
