import React, { Component } from 'react';

class AddUser extends Component {

    componentDidMount() {
        document.title = 'Add User';
    }
    render(){
        return(

            <div className="container">
                <div className="jumbotron">
                    <h1 className="text-center">Add User</h1>
                </div>
            </div>
        );

    }
}

class UserForm extends Component {
    render() {
        return (
            <div>
                <form id='form1'>
                <div class="form-group">
                    <label for="Name">Name:</label>
                    <input type="text" class="form-control" id="name" placeholder="Enter your name" name="name"></input>
                </div>
                <div class="form-group">
                    <label for="age">Age:</label>
                    <input type="number" class="form-control" id="age" placeholder="Enter your age" name="age"></input>
                </div>
                <button type="submit" class="btn btn-success" style="border-radius:15px;">Submit</button>
            </form>
            </div>
        );
    }
}

export default AddUser;
