import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'
import AddUser from './AddUser';
import ViewUser from './ViewUser';
import UpdateUser from './UpdateUser';
import DeleteUser from './DeleteUser';

class App extends Component {
  render() {

    const routing = (
      <Router>
        <div>
          <Route path="/" component={Home} />
          <Route path="/addUser" component={AddUser} />
          <Route path="/viewUser" component={ViewUser} />
          <Route path="/updateUser" component={UpdateUser} />
          <Route path="/DeleteUser" component={DeleteUser} />
        </div>
      </Router>
    )

    return  <Home />;
  }
}

class Home extends Component {
  render() {
    return (
      <div className="container">
        <div className="jumbotron">
          <h1 className="text-center">Admin Panel for Users</h1>
        </div>
        <AdminButtons />
      </div>
    );
  }
}

class AdminButtons extends Component {
  render(){
    return(
      <div class="row">
        <div class="col-md-3">
          <a href="/addUser" class="btn btn-success" style="border-radius:15px;">Add User</a>
        </div>
        <div class="col-md-3">
          <a href="/viewUser" class="btn btn-info" style="border-radius:15px;">View User</a>
        </div>
        <div class="col-md-3">
          <a href="/updateUser" class="btn btn-primary" style="border-radius:15px;">Update User</a>
        </div>
        <div class="col-md-3">
          <a href="/deleteUser" class="btn btn-danger" style="border-radius:15px;">Delete User</a>
        </div>
      </div>
    );
  }
}

class Routing extends Component {
  render(){

  }
}

export default App;
